import{_ as a,M as e,p as i,q as o,R as t,t as s,N as d,a1 as r}from"./framework-ea2a9e6e.js";const l={},g=r('<h1 id="软件基本操作" tabindex="-1"><a class="header-anchor" href="#软件基本操作" aria-hidden="true">#</a> 软件基本操作</h1><h2 id="工程结构" tabindex="-1"><a class="header-anchor" href="#工程结构" aria-hidden="true">#</a> 工程结构</h2><ul><li><code>init.lua</code> 工程配置文件</li><li><code>icon.png</code> 工程图标文件</li><li><code>main.lua</code> 工程主入口文件</li><li><code>layout.aly</code> 工程默认创建的布局文件</li></ul><h3 id="init-lua-配置" tabindex="-1"><a class="header-anchor" href="#init-lua-配置" aria-hidden="true">#</a> init.lua 配置</h3><table><thead><tr><th>键</th><th>说明</th></tr></thead><tbody><tr><td>appname</td><td><strong>string</strong>: 应用名</td></tr><tr><td>appver</td><td><strong>string</strong>: 版本名</td></tr><tr><td>appcode</td><td><strong>string</strong>: 版本号</td></tr><tr><td>packagename</td><td><strong>string</strong>: 应用包名</td></tr><tr><td>appsdk</td><td><strong>string</strong>: 等你来补充</td></tr><tr><td>path_pattern</td><td><strong>string</strong>: 等你来补充</td></tr><tr><td>theme</td><td><strong>string</strong>: 主题</td></tr><tr><td>app_key</td><td><strong>string</strong>: 等你来补充</td></tr><tr><td>app_channel</td><td><strong>string</strong>: 等你来补充</td></tr><tr><td>developer</td><td><strong>string</strong>: 开发者名称</td></tr><tr><td>description</td><td><strong>string</strong>: 应用描述</td></tr><tr><td>debugmode</td><td><strong>boolean</strong>: 调试模式（影响 <code>print</code> 与报错的提示显示）</td></tr><tr><td>user_permission</td><td><strong>table (list)</strong>: 权限列表，不加 <code>android.permission</code> 前缀</td></tr></tbody></table>',5),h={href:"https://docs.oracle.com/javase/tutorial/java/package/namingpkgs.html",target:"_blank",rel:"noopener noreferrer"},c=r('<div class="custom-container tip"><p class="custom-container-title">提示</p><p>您可以使用“属性”功能可视化编辑此文件</p></div><h2 id="菜单功能" tabindex="-1"><a class="header-anchor" href="#菜单功能" aria-hidden="true">#</a> 菜单功能</h2><ul><li><strong>三角形</strong> 运行：执行当前工程</li><li><strong>左箭头</strong> 撤销：撤销输入的内容</li><li><strong>右箭头</strong> 重做：恢复撤销的内容</li><li><strong>打开</strong>：打开文件，在文件列表长按可删除文件</li><li><strong>最近</strong>：显示最近打开过的文件</li></ul><h2 id="文件" tabindex="-1"><a class="header-anchor" href="#文件" aria-hidden="true">#</a> 文件</h2><ul><li><strong>保存</strong>：保存当前文件</li><li><strong>新建</strong>：新建lua代码文件或者aly布局文件，代码文件与布局文件文件名不可以相同</li><li><strong>编译</strong>：把当前文件编译为luac文件，通常用不到</li></ul><h2 id="工程" tabindex="-1"><a class="header-anchor" href="#工程" aria-hidden="true">#</a> 工程</h2><ul><li><strong>代开</strong>：在工程列表打开工程</li><li><strong>打包</strong>：将当前工程编译为apk，默认使用debug签名</li><li><strong>新建</strong>：新建一个工程</li><li><strong>导出</strong>：将当前工程备份为alp文件</li><li><strong>属性</strong>：编辑当前工程的属性，如 名称 权限等</li></ul><h2 id="代码" tabindex="-1"><a class="header-anchor" href="#代码" aria-hidden="true">#</a> 代码</h2><ul><li><strong>格式化</strong>：重新缩进当前文件使其更加便于阅读</li><li><strong>导入分析</strong>：分析当前文件及引用文件需要导入的java类</li><li><strong>查错</strong>：检查当前文件是否有语法错误</li></ul><h2 id="转到" tabindex="-1"><a class="header-anchor" href="#转到" aria-hidden="true">#</a> 转到</h2><ul><li><strong>搜索</strong>：搜索指定内容位置</li><li><strong>转到</strong>：按行号跳转</li><li><strong>导航</strong>：按函数跳转</li></ul><h2 id="插件" tabindex="-1"><a class="header-anchor" href="#插件" aria-hidden="true">#</a> 插件</h2><ul><li>使用安装的插件</li></ul><h2 id="其他" tabindex="-1"><a class="header-anchor" href="#其他" aria-hidden="true">#</a> 其他</h2><ul><li><strong>布局助手</strong>：在编辑器打开 aly 文件时用于设计布局，目前功能尚不完善</li><li><strong>日志</strong>：查看程序运行时的日志</li><li><strong>java浏览器</strong>：用于查看 Java 类的方法</li><li><strong>手册</strong>：离线版 Lua 官方手册</li><li><strong>联系作者</strong>：加入官方 QQ 群与作者交流</li><li><strong>捐赠</strong>：使用支付宝捐赠作者，使软件更好的发展下去</li></ul>',15);function u(p,_){const n=e("ExternalLinkIcon");return i(),o("div",null,[g,t("p",null,[t("a",h,[s("命名包 (Naming a Package)"),d(n)])]),c])}const m=a(l,[["render",u],["__file","base.html.vue"]]);export{m as default};
